library(tidyverse)
library(ggbeeswarm)
library(ggrepel)
library(ggalt)
library(ggtext)
library(waffle)
library(patchwork)
seasonsSorted &lt;- read_csv("season_stats_withtime.csv")

only23 &lt;- seasonsSorted |&gt;
  filter(season == 2023)

only24 &lt;- seasonsSorted |&gt;
  filter(season == 2024) |&gt;
  mutate(
    avg_points = sum(team_points) / 812
  )
only25 &lt;- seasonsSorted |&gt;
  filter(season == 2025)

nu23 &lt;- only23 |&gt;
  filter(school == "Nebraska")

uo23 &lt;- only23 |&gt;
  filter(school == "Oregon")

