  filter(school == "Iowa")

big23 &lt;- only23 |&gt;
  filter(conference == "Big Ten")

sec23 &lt;- only23 |&gt;
  filter(conference == "SEC")

acc23 &lt;- only23 |&gt;
  filter(conference == "ACC")

bTwelve23 &lt;- only23 |&gt;
  filter(conference == "Big 12")

nu24 &lt;- only24 |&gt;
  filter(school == "Nebraska")

uo24 &lt;- only24 |&gt;
  filter(school == "Oregon")

osu24 &lt;- only24 |&gt;
