all24E &lt;- only24 |&gt;
  filter(time_slot == "Evening") |&gt;
  mutate(
    avg_a24E = sum(team_points) / 352
  )

all24A &lt;- only24 |&gt;
  filter(time_slot == "Afternoon") |&gt;
  mutate(
    avg_a24E = sum(team_points) / 251
  )

all24N &lt;- only24 |&gt;
  filter(time_slot == "Noon") |&gt;
  mutate(
    avg_a24E = sum(team_points) / 209
  )

acc24 &lt;- only24 |&gt;
  filter(conference == "ACC")

bTwelve24 &lt;- only24 |&gt;
  filter(conference == "Big 12")

nu25 &lt;- only25 |&gt;
  filter(school == "Nebraska")

