acc25 &lt;- only25 |&gt;
  filter(conference == "ACC")

bTwelve25 &lt;- only25 |&gt;
  filter(conference == "Big 12")

bigEslice &lt;- big25 |&gt;
  filter(time_slot == "Evening") |&gt;
  summarise(
    total_games = n()
  )

bigAslice &lt;- big25 |&gt;
